import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import "./Expenses.css";
import { useState } from "react";

const Expenses = (props) => {
  const [filterYear, setFilterYear] = useState("2021");

  const filterChangeHandler = (selectedYear) => {
    setFilterYear(selectedYear);
  };

  const filteredExpenses = props.items.filter((exp) => {
    return exp.date.getFullYear().toString() === filterYear;
  });

  let expensesContent = <p>No expenses found this year</p>;

  if (filteredExpenses.length > 0) {
    expensesContent = filteredExpenses.map((exp) => {
      return (
        <ExpenseItem 
          key={exp.id}
          title={exp.title} 
          amount={exp.amount} 
          date={exp.date}
        />
    )})
  };
 
  return (
    <Card className="expenses">
      <ExpensesFilter onChangeFilter={filterChangeHandler} filterYear={filterYear}/>

      {expensesContent}

    </Card>
  )
};
 
export default Expenses;
